package week1.day1;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int num=30,i=2;
        if(num==0||num==1||num%i>0) {
    	   System.out.println(num+ " is a not prime number");
    	  
    	   }
      
        else if(num%i==0) {
    		   System.out.println(num+" is a prime number");
    		  
    	   }
      
    	  
       }
	}


